-- Stage 3, Exercise 1: Storytelling Narration (Complete Data - ALL 10 items)
INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '3.1.1', 13, 'Tell me about a time you felt really happy.', 'مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ واقعی خوش تھے۔', 'Narrate a personal experience of happiness with past tense storytelling', 'ماضی کے زمانے کی کہانی کے ساتھ خوشی کے ذاتی تجربے کی روایت', 1, '{"prompt": "Tell me about a time you felt really happy.", "prompt_urdu": "مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ واقعی خوش تھے۔", "category": "emotional_experiences", "difficulty": "b1_intermediate", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["was", "were", "felt", "happy", "because", "when", "then", "finally"], "example_keywords_urdu": ["تھا", "تھے", "محسوس", "خوش", "کیونکہ", "جب", "پھر", "آخر میں"], "model_answer": "I was really happy when I got my first job. I had been looking for work for months, and when I finally got the call, I felt excited and relieved. Then I celebrated with my family, and finally I started working the next week.", "model_answer_urdu": "میں واقعی خوش تھا جب مجھے اپنی پہلی نوکری ملی۔ میں مہینوں سے کام تلاش کر رہا تھا، اور جب آخر میں مجھے کال آئی، تو میں پرجوش اور مطمئن محسوس کر رہا تھا۔ پھر میں نے اپنے خاندان کے ساتھ جشن منایا، اور آخر میں میں نے اگلے ہفتے کام شروع کر دیا۔", "evaluation_criteria": {"past_tense_usage": ["was", "were", "had", "got", "felt", "started"], "narrative_structure": ["when", "then", "finally", "because"], "descriptive_language": ["really", "excited", "relieved", "celebrated"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'' ''Then,'' ''Finally''"], "type": "storytelling"}', 1, 'emotional_experiences', 'b1_intermediate'),
('topic', '3.1.2', 13, 'Describe a fun day you had recently.', 'ایک تفریحی دن کی تفصیل بتائیں جو آپ نے حال ہی میں گزارا۔', 'Narrate a recent enjoyable day with detailed storytelling', 'تفصیلی کہانی کے ساتھ حالیہ لطف اندوز دن کی روایت', 2, '{"prompt": "Describe a fun day you had recently.", "prompt_urdu": "ایک تفریحی دن کی تفصیل بتائیں جو آپ نے حال ہی میں گزارا۔", "category": "recent_experiences", "difficulty": "b1_intermediate", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["went", "with friends", "park", "games", "fun", "enjoyed", "played", "ate"], "example_keywords_urdu": ["گئے", "دوستوں کے ساتھ", "پارک", "کھیل", "تفریح", "لطف", "کھیلا", "کھایا"], "model_answer": "I went to the park with my friends last weekend. We played cricket and had a picnic under the trees. Then we walked around the lake and took some photos. Finally, we ate ice cream and went home feeling very happy.", "model_answer_urdu": "میں پچھلے ہفتے اپنے دوستوں کے ساتھ پارک گیا۔ ہم نے کرکٹ کھیلی اور درختوں کے نیچے پکنک کی۔ پھر ہم جھیل کے ارد گرد گھومے اور کچھ تصاویر لیں۔ آخر میں، ہم نے آئس کریم کھائی اور بہت خوش محسوس کرتے ہوئے گھر گئے۔", "evaluation_criteria": {"past_tense_usage": ["went", "played", "had", "walked", "took", "ate", "felt"], "narrative_structure": ["last weekend", "then", "finally", "under", "around"], "descriptive_language": ["fun", "picnic", "trees", "lake", "photos", "happy"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'' ''Then,'' ''Finally''"], "type": "storytelling"}', 2, 'recent_experiences', 'b1_intermediate'),
('topic', '3.1.3', 13, 'Talk about your favorite memory from school.', 'سکول سے اپنے پسندیدہ یاد کے بارے میں بات کریں۔', 'Share a cherished school memory with narrative structure', 'کہانی کی ساخت کے ساتھ پیاری سکول کی یاد شیئر کریں', 3, '{"prompt": "Talk about your favorite memory from school.", "prompt_urdu": "سکول سے اپنے پسندیدہ یاد کے بارے میں بات کریں۔", "category": "school_memories", "difficulty": "b1_intermediate", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["teacher", "played", "friends", "learned", "project", "class", "remember", "loved"], "example_keywords_urdu": ["استاد", "کھیلا", "دوست", "سیکھا", "پروجیکٹ", "کلاس", "یاد", "پسند"], "model_answer": "My favorite memory from school was when our class won the science competition. Our teacher helped us prepare for weeks, and we worked really hard on our project. Then we presented it to the judges, and finally we won first prize. I still remember how proud we all felt.", "model_answer_urdu": "سکول سے میری پسندیدہ یاد یہ تھی جب ہماری کلاس نے سائنس مقابلہ جیتا۔ ہمارے استاد نے ہماری ہفتوں تک تیاری میں مدد کی، اور ہم نے اپنے پروجیکٹ پر بہت محنت کی۔ پھر ہم نے اسے ججوں کے سامنے پیش کیا، اور آخر میں ہم نے پہلا انعام جیتا۔ مجھے اب بھی یاد ہے کہ ہم سب کتنے فخر محسوس کر رہے تھے۔", "evaluation_criteria": {"past_tense_usage": ["was", "won", "helped", "worked", "presented", "felt", "remember"], "narrative_structure": ["when", "for weeks", "then", "finally", "still"], "descriptive_language": ["favorite", "science competition", "really hard", "proud"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'' ''Then,'' ''Finally''"], "type": "storytelling"}', 3, 'school_memories', 'b1_intermediate'),
('topic', '3.1.4', 13, 'Tell me about a time you helped someone.', 'مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ نے کسی کی مدد کی۔', 'Narrate an experience of helping others with detailed storytelling', 'تفصیلی کہانی کے ساتھ دوسروں کی مدد کرنے کے تجربے کی روایت', 4, '{"prompt": "Tell me about a time you helped someone.", "prompt_urdu": "مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ نے کسی کی مدد کی۔", "category": "helping_others", "difficulty": "b1_intermediate", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["helped", "problem", "friend", "thank", "felt good", "because", "when", "then"], "example_keywords_urdu": ["مدد", "مسئلہ", "دوست", "شکریہ", "اچھا محسوس", "کیونکہ", "جب", "پھر"], "model_answer": "I helped my neighbor when she was carrying heavy groceries. I saw her struggling with the bags, so I offered to help carry them to her apartment. Then I helped her put the groceries away, and finally she thanked me and gave me some cookies. I felt really good about helping her.", "model_answer_urdu": "میں نے اپنی پڑوسی کی مدد کی جب وہ بھاری گروسری اٹھا رہی تھی۔ میں نے دیکھا کہ وہ بیگوں کے ساتھ مشکل میں ہے، اس لیے میں نے اس کی اپارٹمنٹ تک بیگ اٹھانے میں مدد کی پیشکش کی۔ پھر میں نے اس کی گروسری رکھنے میں مدد کی، اور آخر میں اس نے میرا شکریہ ادا کیا اور مجھے کچھ کوکیز دیں۔ مجھے اس کی مدد کرنے پر بہت اچھا محسوس ہوا۔", "evaluation_criteria": {"past_tense_usage": ["helped", "was carrying", "saw", "offered", "put", "thanked", "gave", "felt"], "narrative_structure": ["when", "so", "then", "finally", "about"], "descriptive_language": ["heavy", "struggling", "really good", "cookies"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'' ''Then,'' ''Finally''"], "type": "storytelling"}', 4, 'helping_others', 'b1_intermediate'),
('topic', '3.1.5', 13, 'Describe a time you learned something new.', 'ایک ایسے وقت کی تفصیل بتائیں جب آپ نے کچھ نیا سیکھا۔', 'Narrate a learning experience with structured storytelling', 'ساختہ کہانی کے ساتھ سیکھنے کے تجربے کی روایت', 5, '{"prompt": "Describe a time you learned something new.", "prompt_urdu": "ایک ایسے وقت کی تفصیل بتائیں جب آپ نے کچھ نیا سیکھا۔", "category": "learning_experiences", "difficulty": "b1_intermediate", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["learned", "difficult", "practiced", "improved", "teacher", "class", "finally", "proud"], "example_keywords_urdu": ["سیکھا", "مشکل", "مشق", "بہتر", "استاد", "کلاس", "آخر میں", "فخر"], "model_answer": "I learned to cook biryani last year. At first, it was very difficult because I had never cooked before. I practiced the recipe many times, and my mother helped me understand the steps. Then I started to improve, and finally I made a perfect biryani for my family. I was so proud of myself.", "model_answer_urdu": "میں نے پچھلے سال بریانی پکانا سیکھا۔ پہلے تو یہ بہت مشکل تھا کیونکہ میں نے پہلے کبھی کھانا نہیں پکایا تھا۔ میں نے نسخہ کئی بار مشق کیا، اور میری ماں نے مجھے مراحل سمجھنے میں مدد کی۔ پھر میں بہتر ہونے لگا، اور آخر میں میں نے اپنے خاندان کے لیے ایک کامل بریانی بنائی۔ مجھے اپنے آپ پر بہت فخر تھا۔", "evaluation_criteria": {"past_tense_usage": ["learned", "was", "had never", "practiced", "helped", "started", "made", "was proud"], "narrative_structure": ["at first", "because", "then", "finally", "for"], "descriptive_language": ["difficult", "many times", "perfect", "proud"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'' ''Then,'' ''Finally''"], "type": "storytelling"}', 5, 'learning_experiences', 'b1_intermediate'),
('topic', '3.1.6', 13, 'Tell me about a time you traveled somewhere interesting.', 'مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ کہیں دلچسپ جگہ گئے۔', 'Narrate a travel experience with descriptive storytelling', 'تفصیلی کہانی کے ساتھ سفر کے تجربے کی روایت', 6, '{"prompt": "Tell me about a time you traveled somewhere interesting.", "prompt_urdu": "مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ کہیں دلچسپ جگہ گئے۔", "category": "travel_experiences", "difficulty": "b1_intermediate", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["traveled", "visited", "saw", "beautiful", "place", "enjoyed", "tried", "food"], "example_keywords_urdu": ["سفر", "ملاقات", "دیکھا", "خوبصورت", "جگہ", "لطف", "آزمایا", "کھانا"], "model_answer": "I traveled to Lahore last summer with my family. We visited the beautiful Badshahi Mosque and saw the historic Lahore Fort. Then we tried the famous food at Food Street, and finally we went shopping at the traditional markets. I enjoyed every moment of that trip.", "model_answer_urdu": "میں پچھلی گرمیوں میں اپنے خاندان کے ساتھ لاہور گیا۔ ہم نے خوبصورت بادشاہی مسجد دیکھی اور تاریخی لاہور قلعہ دیکھا۔ پھر ہم نے فوڈ سٹریٹ پر مشہور کھانا آزمایا، اور آخر میں ہم روایتی مارکیٹوں میں خریداری کے لیے گئے۔ مجھے اس سفر کا ہر لمحہ پسند آیا۔", "evaluation_criteria": {"past_tense_usage": ["traveled", "visited", "saw", "tried", "went", "enjoyed"], "narrative_structure": ["last summer", "then", "finally", "at", "of"], "descriptive_language": ["beautiful", "historic", "famous", "traditional", "every moment"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'' ''Then,'' ''Finally''"], "type": "storytelling"}', 6, 'travel_experiences', 'b1_intermediate'),
('topic', '3.1.7', 13, 'Describe a time you overcame a challenge.', 'ایک ایسے وقت کی تفصیل بتائیں جب آپ نے ایک چیلنج پر قابو پایا۔', 'Narrate overcoming a challenge with motivational storytelling', 'حوصلہ افزا کہانی کے ساتھ چیلنج پر قابو پانے کی روایت', 7, '{"prompt": "Describe a time you overcame a challenge.", "prompt_urdu": "ایک ایسے وقت کی تفصیل بتائیں جب آپ نے ایک چیلنج پر قابو پایا۔", "category": "personal_growth", "difficulty": "b1_intermediate", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["challenge", "difficult", "worked hard", "practiced", "succeeded", "proud", "because", "finally"], "example_keywords_urdu": ["چیلنج", "مشکل", "سخت محنت", "مشق", "کامیاب", "فخر", "کیونکہ", "آخر میں"], "model_answer": "I had a difficult time learning to drive. At first, I was very nervous and made many mistakes. I practiced every day with my father, and I worked hard to improve my skills. Then I started to feel more confident, and finally I passed my driving test on the first try. I was so proud of my achievement.", "model_answer_urdu": "مجھے گاڑی چلانا سیکھنے میں مشکل وقت تھا۔ پہلے تو میں بہت گھبرایا ہوا تھا اور بہت سی غلطیاں کرتا تھا۔ میں نے اپنے والد کے ساتھ روزانہ مشق کی، اور میں نے اپنی مہارتوں کو بہتر بنانے کے لیے سخت محنت کی۔ پھر میں زیادہ پراعتماد محسوس کرنے لگا، اور آخر میں میں نے پہلی کوشش میں اپنا ڈرائیونگ ٹیسٹ پاس کر لیا۔ مجھے اپنی کامیابی پر بہت فخر تھا۔", "evaluation_criteria": {"past_tense_usage": ["had", "was", "made", "practiced", "worked", "started", "passed", "was proud"], "narrative_structure": ["at first", "every day", "then", "finally", "on the first try"], "descriptive_language": ["difficult", "nervous", "confident", "achievement"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'' ''Then,'' ''Finally''"], "type": "storytelling"}', 7, 'personal_growth', 'b1_intermediate'),
('topic', '3.1.8', 13, 'Tell me about a time you made a new friend.', 'مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ نے نیا دوست بنایا۔', 'Narrate making a new friendship with social storytelling', 'سماجی کہانی کے ساتھ نئی دوستی بنانے کی روایت', 8, '{"prompt": "Tell me about a time you made a new friend.", "prompt_urdu": "مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ نے نیا دوست بنایا۔", "category": "social_interactions", "difficulty": "b1_intermediate", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["met", "friend", "talked", "shared", "interests", "became", "close", "still"], "example_keywords_urdu": ["ملے", "دوست", "بات", "شریک", "دلچسپیاں", "بن گئے", "قریب", "اب بھی"], "model_answer": "I met my best friend Ahmed at the library last year. We were both reading the same book, so we started talking about it. Then we discovered we had many common interests, and finally we became close friends. We still meet every weekend to study together.", "model_answer_urdu": "میں نے اپنے بہترین دوست احمد سے پچھلے سال لائبریری میں ملاقات کی۔ ہم دونوں ایک ہی کتاب پڑھ رہے تھے، اس لیے ہم نے اس کے بارے میں بات شروع کی۔ پھر ہمیں پتہ چلا کہ ہمارے بہت سے مشترکہ دلچسپیاں ہیں، اور آخر میں ہم قریب دوست بن گئے۔ ہم اب بھی ہر ہفتے ایک ساتھ پڑھنے کے لیے ملتے ہیں۔", "evaluation_criteria": {"past_tense_usage": ["met", "were reading", "started", "discovered", "became", "still meet"], "narrative_structure": ["last year", "so", "then", "finally", "every weekend"], "descriptive_language": ["best friend", "common interests", "close friends", "together"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'' ''Then,'' ''Finally''"], "type": "storytelling"}', 8, 'social_interactions', 'b1_intermediate'),
('topic', '3.1.9', 13, 'Describe a time you achieved something important.', 'ایک ایسے وقت کی تفصیل بتائیں جب آپ نے کچھ اہم حاصل کیا۔', 'Narrate achieving an important goal with inspirational storytelling', 'حوصلہ افزا کہانی کے ساتھ اہم ہدف حاصل کرنے کی روایت', 9, '{"prompt": "Describe a time you achieved something important.", "prompt_urdu": "ایک ایسے وقت کی تفصیل بتائیں جب آپ نے کچھ اہم حاصل کیا۔", "category": "achievements", "difficulty": "b1_intermediate", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["achieved", "goal", "worked hard", "studied", "passed", "exam", "graduated", "celebrated"], "example_keywords_urdu": ["حاصل", "ہدف", "سخت محنت", "پڑھائی", "پاس", "امتحان", "گریجویٹ", "جشن"], "model_answer": "I achieved my goal of graduating from university last year. I had worked hard for four years, studying late at night and attending all my classes. Then I took my final exams and passed them all with good grades. Finally, I graduated with honors and celebrated with my family. It was the proudest moment of my life.", "model_answer_urdu": "میں نے پچھلے سال یونیورسٹی سے گریجویشن کا اپنا ہدف حاصل کیا۔ میں نے چار سال سخت محنت کی تھی، رات دیر تک پڑھائی کرتے ہوئے اور اپنی تمام کلاسز میں شرکت کرتے ہوئے۔ پھر میں نے اپنے آخری امتحانات دیے اور اچھے گریڈز کے ساتھ سب پاس کر لیے۔ آخر میں، میں نے اعزاز کے ساتھ گریجویشن کی اور اپنے خاندان کے ساتھ جشن منایا۔ یہ میری زندگی کا سب سے فخر کا لمحہ تھا۔", "evaluation_criteria": {"past_tense_usage": ["achieved", "had worked", "studying", "took", "passed", "graduated", "celebrated", "was"], "narrative_structure": ["last year", "for four years", "then", "finally", "of my life"], "descriptive_language": ["goal", "hard", "late at night", "good grades", "honors", "proudest"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'' ''Then,'' ''Finally''"], "type": "storytelling"}', 9, 'achievements', 'b1_intermediate'),
('topic', '3.1.10', 13, 'Tell me about a time you felt scared or nervous.', 'مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ خوفزدہ یا گھبرائے ہوئے تھے۔', 'Narrate a nervous experience with emotional storytelling', 'جذباتی کہانی کے ساتھ گھبراہٹ کے تجربے کی روایت', 10, '{"prompt": "Tell me about a time you felt scared or nervous.", "prompt_urdu": "مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ خوفزدہ یا گھبرائے ہوئے تھے۔", "category": "emotional_experiences", "difficulty": "b1_intermediate", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["felt", "scared", "nervous", "worried", "because", "when", "then", "finally"], "example_keywords_urdu": ["محسوس", "خوفزدہ", "گھبرایا", "پریشان", "کیونکہ", "جب", "پھر", "آخر میں"], "model_answer": "I felt very nervous when I had to give a presentation in front of my class. I was worried because I had never spoken in public before. Then I practiced my speech many times with my friends, and finally I gave the presentation successfully. I felt relieved and proud afterward.", "model_answer_urdu": "میں بہت گھبرایا ہوا تھا جب مجھے اپنی کلاس کے سامنے پریزنٹیشن دینی تھی۔ میں پریشان تھا کیونکہ میں نے پہلے کبھی عوامی طور پر بات نہیں کی تھی۔ پھر میں نے اپنے دوستوں کے ساتھ اپنی تقریر کئی بار مشق کی، اور آخر میں میں نے پریزنٹیشن کامیابی سے دی۔ اس کے بعد میں مطمئن اور فخر محسوس کر رہا تھا۔", "evaluation_criteria": {"past_tense_usage": ["felt", "had to", "was worried", "had never", "practiced", "gave", "felt"], "narrative_structure": ["when", "because", "then", "finally", "afterward"], "descriptive_language": ["very nervous", "worried", "many times", "successfully", "relieved"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'' ''Then,'' ''Finally''"], "type": "storytelling"}', 10, 'emotional_experiences', 'b1_intermediate');

