-- Count topics for Stage 3, Exercise 1
SELECT COUNT(*) as total_topics FROM public.ai_tutor_content_hierarchy 
WHERE level = 'topic' AND hierarchy_path LIKE '3.1.%';

-- Show all topics for Stage 3, Exercise 1
SELECT topic_number, title, title_urdu, category, difficulty 
FROM public.ai_tutor_content_hierarchy 
WHERE level = 'topic' AND hierarchy_path LIKE '3.1.%' 
ORDER BY topic_number;

-- Summary of Stage 3, Exercise 1 data
SELECT 
    'Stage 3, Exercise 1: Storytelling Narration' as exercise_name,
    COUNT(*) as total_items,
    STRING_AGG(DISTINCT category, ', ') as categories_covered
FROM public.ai_tutor_content_hierarchy 
WHERE level = 'topic' AND hierarchy_path LIKE '3.1.%';
