INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '3.2.1', 14, 'Planning a Weekend Trip', 'ہفتے کے آخر میں سفر کی منصوبہ بندی', 'Simulate group decision-making for weekend travel planning', 'ہفتے کے آخر میں سفر کی منصوبہ بندی کے لیے گروپ فیصلہ سازی کی مشق', 1, '{"title": "Planning a Weekend Trip", "title_urdu": "ہفتے کے آخر میں سفر کی منصوبہ بندی", "context": "You and your friends (AI) are planning a weekend trip.", "context_urdu": "آپ اور آپ کے دوست (AI) ہفتے کے آخر میں سفر کی منصوبہ بندی کر رہے ہیں۔", "difficulty": "b1_intermediate", "category": "travel_planning", "conversation_flow": "decision_making", "initial_prompt": "Ali: Where should we go this weekend?", "initial_prompt_urdu": "علی: ہمیں اس ہفتے کے آخر میں کہاں جانا چاہیے؟", "follow_up_turns": [{"speaker": "Sara", "message": "I think the beach would be fun! We could swim and have a picnic.", "message_urdu": "میں سوچتی ہوں کہ ساحل سمندر تفریحی ہوگا! ہم تیر سکتے ہیں اور پکنک کر سکتے ہیں۔", "intent": "suggestion_with_reason"}, {"speaker": "Ali", "message": "What do you think, [your name]? Should we go to the beach or somewhere else?", "message_urdu": "آپ کیا سوچتے ہیں، [آپ کا نام]؟ کیا ہمیں ساحل سمندر جانا چاہیے یا کہیں اور؟", "intent": "asking_for_opinion"}], "expected_responses": [{"response": "I agree! The beach sounds perfect for a weekend trip.", "response_urdu": "میں متفق ہوں! ہفتے کے آخر میں سفر کے لیے ساحل سمندر بہترین لگتا ہے۔", "type": "agreement", "keywords": ["agree", "beach", "perfect", "weekend"]}, {"response": "That''s a great idea! I love swimming and picnics.", "response_urdu": "یہ ایک بہترین خیال ہے! مجھے تیراکی اور پکنک پسند ہے۔", "type": "agreement_with_enthusiasm", "keywords": ["great", "idea", "love", "swimming"]}, {"response": "Actually, I was thinking about the mountains. What do you think about hiking?", "response_urdu": "دراصل، میں پہاڑوں کے بارے میں سوچ رہا تھا۔ پیدل سفر کے بارے میں آپ کیا سوچتے ہیں؟", "type": "polite_disagreement_with_alternative", "keywords": ["actually", "mountains", "hiking", "think"]}], "evaluation_criteria": {"relevance_to_conversation": ["responds_to_question", "acknowledges_others", "contributes_meaningfully"], "appropriate_expressions": ["agreement_phrases", "disagreement_phrases", "opinion_phrases"], "fluency_and_tone": ["natural_flow", "polite_tone", "clear_pronunciation"], "conversation_timing": ["responds_appropriately", "doesnt_interrupt", "maintains_flow"], "minimum_score": 75}, "learning_objectives": ["Improve conversational flow and timing", "Learn how to agree/disagree politely", "Practice group decision-making language", "Develop ability to speak within a contextual back-and-forth"], "type": "group_discussion"}', 1, 'travel_planning', 'b1_intermediate'),
('topic', '3.2.2', 14, 'Choosing a Movie', 'فلم کا انتخاب', 'Simulate group movie selection discussion with preferences', 'ترجیحات کے ساتھ گروپ فلم انتخاب کی بحث کی مشق', 2, '{"title": "Choosing a Movie", "title_urdu": "فلم کا انتخاب", "context": "You''re discussing what movie to watch with two friends.", "context_urdu": "آپ دو دوستوں کے ساتھ کون سی فلم دیکھنی ہے اس پر بحث کر رہے ہیں۔", "difficulty": "b1_intermediate", "category": "entertainment_choice", "conversation_flow": "preference_discussion", "initial_prompt": "Raza: Do you want to watch an action movie or comedy?", "initial_prompt_urdu": "رضا: کیا آپ ایکشن فلم دیکھنا چاہتے ہیں یا کامیڈی؟", "follow_up_turns": [{"speaker": "Maya", "message": "I vote for comedy. It''s been a stressful week and I need to laugh!", "message_urdu": "میں کامیڈی کے لیے ووٹ دیتی ہوں۔ یہ ایک تناؤ والا ہفتہ رہا ہے اور مجھے ہنسنے کی ضرورت ہے!", "intent": "preference_with_reason"}, {"speaker": "Raza", "message": "Hmm, what do you think? Should we go with comedy or do you prefer action?", "message_urdu": "ہم، آپ کیا سوچتے ہیں؟ کیا ہمیں کامیڈی کے ساتھ جانا چاہیے یا آپ ایکشن کو ترجیح دیتے ہیں؟", "intent": "seeking_consensus"}], "expected_responses": [{"response": "Comedy sounds great! I could use a good laugh too.", "response_urdu": "کامیڈی بہترین لگتی ہے! مجھے بھی اچھی ہنسی کی ضرورت ہے۔", "type": "agreement_with_empathy", "keywords": ["comedy", "great", "laugh", "too"]}, {"response": "I prefer action movies, but I''m okay with comedy since you both want it.", "response_urdu": "میں ایکشن فلموں کو ترجیح دیتا ہوں، لیکن میں کامیڈی کے ساتھ ٹھیک ہوں کیونکہ آپ دونوں یہ چاہتے ہیں۔", "type": "compromise", "keywords": ["prefer", "action", "okay", "since"]}, {"response": "Actually, I was thinking about a thriller. What about that?", "response_urdu": "دراصل، میں تھرلر کے بارے میں سوچ رہا تھا۔ اس کے بارے میں کیا خیال ہے؟", "type": "suggestion_with_alternative", "keywords": ["actually", "thriller", "thinking", "about"]}], "evaluation_criteria": {"relevance_to_conversation": ["responds_to_question", "acknowledges_others", "contributes_meaningfully"], "appropriate_expressions": ["agreement_phrases", "disagreement_phrases", "opinion_phrases"], "fluency_and_tone": ["natural_flow", "polite_tone", "clear_pronunciation"], "conversation_timing": ["responds_appropriately", "doesnt_interrupt", "maintains_flow"], "minimum_score": 75}, "learning_objectives": ["Improve conversational flow and timing", "Learn how to agree/disagree politely", "Practice group decision-making language", "Develop ability to speak within a contextual back-and-forth"], "type": "group_discussion"}', 2, 'entertainment_choice', 'b1_intermediate'),
('topic', '3.2.3', 14, 'Deciding on Dinner Plans', 'رات کے کھانے کی منصوبہ بندی', 'Simulate group dinner planning discussion with food preferences', 'کھانے کی ترجیحات کے ساتھ گروپ ڈنر پلاننگ کی بحث کی مشق', 3, '{"title": "Deciding on Dinner Plans", "title_urdu": "رات کے کھانے کی منصوبہ بندی", "context": "You and your roommates are deciding where to eat dinner.", "context_urdu": "آپ اور آپ کے روم میٹس رات کا کھانا کہاں کھانا ہے اس کا فیصلہ کر رہے ہیں۔", "difficulty": "b1_intermediate", "category": "food_planning", "conversation_flow": "group_decision", "initial_prompt": "Fatima: I''m hungry! Where should we go for dinner?", "initial_prompt_urdu": "فاطمہ: مجھے بھوک لگی ہے! ہمیں رات کا کھانا کہاں جانا چاہیے؟", "follow_up_turns": [{"speaker": "Ahmed", "message": "How about that new Chinese restaurant? I heard it''s really good.", "message_urdu": "اس نئے چینی ریستوران کے بارے میں کیا خیال ہے؟ میں نے سنا ہے کہ یہ واقعی اچھا ہے۔", "intent": "suggestion_with_recommendation"}, {"speaker": "Fatima", "message": "What do you think? Chinese food or something else?", "message_urdu": "آپ کیا سوچتے ہیں؟ چینی کھانا یا کچھ اور؟", "intent": "asking_for_opinion"}], "expected_responses": [{"response": "Chinese sounds perfect! I love trying new restaurants.", "response_urdu": "چینی بہترین لگتا ہے! مجھے نئے ریستورانز آزمانے پسند ہیں۔", "type": "enthusiastic_agreement", "keywords": ["chinese", "perfect", "love", "new"]}, {"response": "I''m not really in the mood for Chinese. What about pizza?", "response_urdu": "مجھے چینی کھانے کا موڈ نہیں ہے۔ پیزا کے بارے میں کیا خیال ہے؟", "type": "polite_disagreement_with_alternative", "keywords": ["mood", "chinese", "pizza", "about"]}, {"response": "I''m fine with Chinese, but I''m also open to other options.", "response_urdu": "میں چینی کے ساتھ ٹھیک ہوں، لیکن میں دوسرے اختیارات کے لیے بھی کھلا ہوں۔", "type": "flexible_agreement", "keywords": ["fine", "chinese", "open", "options"]}], "evaluation_criteria": {"relevance_to_conversation": ["responds_to_question", "acknowledges_others", "contributes_meaningfully"], "appropriate_expressions": ["agreement_phrases", "disagreement_phrases", "opinion_phrases"], "fluency_and_tone": ["natural_flow", "polite_tone", "clear_pronunciation"], "conversation_timing": ["responds_appropriately", "doesnt_interrupt", "maintains_flow"], "minimum_score": 75}, "learning_objectives": ["Improve conversational flow and timing", "Learn how to agree/disagree politely", "Practice group decision-making language", "Develop ability to speak within a contextual back-and-forth"], "type": "group_discussion"}', 3, 'food_planning', 'b1_intermediate'),
('topic', '3.2.4', 14, 'Planning a Study Group', 'مطالعہ گروپ کی منصوبہ بندی', 'Simulate academic study group planning with scheduling', 'شیڈولنگ کے ساتھ تعلیمی مطالعہ گروپ کی منصوبہ بندی کی مشق', 4, '{"title": "Planning a Study Group", "title_urdu": "مطالعہ گروپ کی منصوبہ بندی", "context": "You and your classmates are organizing a study session for an upcoming exam.", "context_urdu": "آپ اور آپ کے کلاس فیلوز آنے والے امتحان کے لیے مطالعہ سیشن کا اہتمام کر رہے ہیں۔", "difficulty": "b1_intermediate", "category": "academic_planning", "conversation_flow": "logistical_decision", "initial_prompt": "Zara: We need to study for the math exam. When should we meet?", "initial_prompt_urdu": "زارا: ہمیں ریاضی کے امتحان کے لیے پڑھنا ہے۔ ہمیں کب ملنا چاہیے؟", "follow_up_turns": [{"speaker": "Hassan", "message": "How about tomorrow afternoon? I''m free from 2 to 5 PM.", "message_urdu": "کل دوپہر کے بارے میں کیا خیال ہے؟ مجھے شام 2 سے 5 بجے تک فارغ ہوں۔", "intent": "time_suggestion"}, {"speaker": "Zara", "message": "What do you think? Does that time work for everyone?", "message_urdu": "آپ کیا سوچتے ہیں؟ کیا وہ وقت سب کے لیے ٹھیک ہے؟", "intent": "seeking_consensus"}], "expected_responses": [{"response": "Tomorrow afternoon works perfectly for me!", "response_urdu": "کل دوپہر میرے لیے بہترین ہے!", "type": "enthusiastic_agreement", "keywords": ["tomorrow", "afternoon", "perfectly", "works"]}, {"response": "I have a class at 3 PM. Could we meet earlier, like 1 PM?", "response_urdu": "مجھے شام 3 بجے کلاس ہے۔ کیا ہم پہلے مل سکتے ہیں، جیسے 1 بجے؟", "type": "polite_conflict_with_solution", "keywords": ["class", "earlier", "could", "meet"]}, {"response": "I''m flexible with the time. Whatever works best for everyone.", "response_urdu": "میں وقت کے ساتھ لچکدار ہوں۔ جو بھی سب کے لیے بہترین ہو۔", "type": "flexible_agreement", "keywords": ["flexible", "time", "works", "everyone"]}], "evaluation_criteria": {"relevance_to_conversation": ["responds_to_question", "acknowledges_others", "contributes_meaningfully"], "appropriate_expressions": ["agreement_phrases", "disagreement_phrases", "opinion_phrases"], "fluency_and_tone": ["natural_flow", "polite_tone", "clear_pronunciation"], "conversation_timing": ["responds_appropriately", "doesnt_interrupt", "maintains_flow"], "minimum_score": 75}, "learning_objectives": ["Improve conversational flow and timing", "Learn how to agree/disagree politely", "Practice group decision-making language", "Develop ability to speak within a contextual back-and-forth"], "type": "group_discussion"}', 4, 'academic_planning', 'b1_intermediate'),
('topic', '3.2.5', 14, 'Choosing a Birthday Gift', 'سالگرہ کا تحفہ منتخب کرنا', 'Simulate collaborative gift selection discussion', 'تعاونی تحفہ انتخاب کی بحث کی مشق', 5, '{"title": "Choosing a Birthday Gift", "title_urdu": "سالگرہ کا تحفہ منتخب کرنا", "context": "You and your friends are discussing what gift to buy for a mutual friend''s birthday.", "context_urdu": "آپ اور آپ کے دوست ایک مشترکہ دوست کی سالگرہ کے لیے کیا تحفہ خریدیں اس پر بحث کر رہے ہیں۔", "difficulty": "b1_intermediate", "category": "gift_planning", "conversation_flow": "collaborative_decision", "initial_prompt": "Layla: What should we get for Amina''s birthday?", "initial_prompt_urdu": "لیلا: ہمیں امینہ کی سالگرہ کے لیے کیا لینا چاہیے؟", "follow_up_turns": [{"speaker": "Omar", "message": "I was thinking about a book. She loves reading mystery novels.", "message_urdu": "میں کتاب کے بارے میں سوچ رہا تھا۔ اسے راز کی ناولز پڑھنا پسند ہے۔", "intent": "suggestion_with_justification"}, {"speaker": "Layla", "message": "That''s a good idea! What do you think? Should we go with a book?", "message_urdu": "یہ ایک اچھا خیال ہے! آپ کیا سوچتے ہیں؟ کیا ہمیں کتاب کے ساتھ جانا چاہیے؟", "intent": "seeking_agreement"}], "expected_responses": [{"response": "A book is perfect! She''ll love it.", "response_urdu": "کتاب بہترین ہے! اسے یہ پسند آئے گی۔", "type": "enthusiastic_agreement", "keywords": ["book", "perfect", "love", "it"]}, {"response": "I think we should get her something more personal, like jewelry.", "response_urdu": "میں سوچتا ہوں کہ ہمیں اسے کچھ زیادہ ذاتی چیز دینی چاہیے، جیسے زیورات۔", "type": "polite_disagreement_with_alternative", "keywords": ["personal", "jewelry", "think", "should"]}, {"response": "A book sounds great, but maybe we could also add a nice bookmark?", "response_urdu": "کتاب بہترین لگتی ہے، لیکن شاید ہم ایک اچھا بک مارک بھی شامل کر سکتے ہیں؟", "type": "agreement_with_enhancement", "keywords": ["book", "great", "bookmark", "could"]}], "evaluation_criteria": {"relevance_to_conversation": ["responds_to_question", "acknowledges_others", "contributes_meaningfully"], "appropriate_expressions": ["agreement_phrases", "disagreement_phrases", "opinion_phrases"], "fluency_and_tone": ["natural_flow", "polite_tone", "clear_pronunciation"], "conversation_timing": ["responds_appropriately", "doesnt_interrupt", "maintains_flow"], "minimum_score": 75}, "learning_objectives": ["Improve conversational flow and timing", "Learn how to agree/disagree politely", "Practice group decision-making language", "Develop ability to speak within a contextual back-and-forth"], "type": "group_discussion"}', 5, 'gift_planning', 'b1_intermediate'),
('topic', '3.2.6', 14, 'Deciding on Weekend Activities', 'ہفتے کے آخر کی سرگرمیوں کا فیصلہ', 'Simulate family weekend activity planning discussion', 'خاندانی ہفتے کے آخر کی سرگرمیوں کی منصوبہ بندی کی بحث کی مشق', 6, '{"title": "Deciding on Weekend Activities", "title_urdu": "ہفتے کے آخر کی سرگرمیوں کا فیصلہ", "context": "You and your family are planning activities for the upcoming weekend.", "context_urdu": "آپ اور آپ کا خاندان آنے والے ہفتے کے آخر کی سرگرمیوں کی منصوبہ بندی کر رہے ہیں۔", "difficulty": "b1_intermediate", "category": "family_planning", "conversation_flow": "family_decision", "initial_prompt": "Mom: What should we do this weekend? Any ideas?", "initial_prompt_urdu": "ماں: ہمیں اس ہفتے کے آخر میں کیا کرنا چاہیے؟ کوئی خیال؟", "follow_up_turns": [{"speaker": "Dad", "message": "How about going to the park? We could have a family picnic.", "message_urdu": "پارک جانے کے بارے میں کیا خیال ہے؟ ہم خاندانی پکنک کر سکتے ہیں۔", "intent": "family_oriented_suggestion"}, {"speaker": "Mom", "message": "That sounds nice! What do you think? Should we go to the park?", "message_urdu": "یہ اچھا لگتا ہے! آپ کیا سوچتے ہیں؟ کیا ہمیں پارک جانا چاہیے؟", "intent": "seeking_family_consensus"}], "expected_responses": [{"response": "I love that idea! A family picnic would be so much fun.", "response_urdu": "مجھے یہ خیال پسند ہے! خاندانی پکنک بہت تفریحی ہوگی۔", "type": "enthusiastic_agreement", "keywords": ["love", "idea", "family", "picnic", "fun"]}, {"response": "Actually, I was hoping we could go to the movies. There''s a new film I want to see.", "response_urdu": "دراصل، میں امید کر رہا تھا کہ ہم فلموں میں جا سکتے ہیں۔ ایک نئی فلم ہے جو میں دیکھنا چاہتا ہوں۔", "type": "polite_disagreement_with_alternative", "keywords": ["actually", "movies", "new", "film", "see"]}, {"response": "The park sounds great! Maybe we could also bring some games?", "response_urdu": "پارک بہترین لگتا ہے! شاید ہم کچھ کھیل بھی لا سکتے ہیں؟", "type": "agreement_with_enhancement", "keywords": ["park", "great", "games", "could"]}], "evaluation_criteria": {"relevance_to_conversation": ["responds_to_question", "acknowledges_others", "contributes_meaningfully"], "appropriate_expressions": ["agreement_phrases", "disagreement_phrases", "opinion_phrases"], "fluency_and_tone": ["natural_flow", "polite_tone", "clear_pronunciation"], "conversation_timing": ["responds_appropriately", "doesnt_interrupt", "maintains_flow"], "minimum_score": 75}, "learning_objectives": ["Improve conversational flow and timing", "Learn how to agree/disagree politely", "Practice group decision-making language", "Develop ability to speak within a contextual back-and-forth"], "type": "group_discussion"}', 6, 'family_planning', 'b1_intermediate'),
('topic', '3.2.7', 14, 'Choosing a Restaurant for Team Lunch', 'ٹیم لنچ کے لیے ریستوران کا انتخاب', 'Simulate professional team lunch restaurant selection', 'پیشہ ورانہ ٹیم لنچ ریستوران انتخاب کی مشق', 7, '{"title": "Choosing a Restaurant for Team Lunch", "title_urdu": "ٹیم لنچ کے لیے ریستوران کا انتخاب", "context": "You and your colleagues are deciding where to have lunch together.", "context_urdu": "آپ اور آپ کے ساتھی مل کر دوپہر کا کھانا کہاں کھائیں اس کا فیصلہ کر رہے ہیں۔", "difficulty": "b1_intermediate", "category": "work_social", "conversation_flow": "professional_decision", "initial_prompt": "Manager: Where should we go for our team lunch today?", "initial_prompt_urdu": "مینیجر: آج ہمیں اپنے ٹیم لنچ کے لیے کہاں جانا چاہیے؟", "follow_up_turns": [{"speaker": "Colleague", "message": "I suggest the new Italian place downtown. The reviews are excellent.", "message_urdu": "میں شہر کے وسط میں نئے اطالوی جگہ کی تجویز دیتا ہوں۔ جائزے بہترین ہیں۔", "intent": "professional_suggestion"}, {"speaker": "Manager", "message": "That sounds promising! What do you think? Should we try the Italian restaurant?", "message_urdu": "یہ امید افزا لگتا ہے! آپ کیا سوچتے ہیں؟ کیا ہمیں اطالوی ریستوران آزمانا چاہیے؟", "intent": "seeking_team_consensus"}], "expected_responses": [{"response": "Italian sounds great! I''ve been wanting to try that place.", "response_urdu": "اطالوی بہترین لگتا ہے! میں اس جگہ کو آزمانا چاہ رہا تھا۔", "type": "enthusiastic_agreement", "keywords": ["italian", "great", "wanting", "try"]}, {"response": "I''m allergic to some Italian ingredients. Could we consider the Indian restaurant instead?", "response_urdu": "مجھے کچھ اطالوی اجزاء سے الرجی ہے۔ کیا ہم ہندوستانی ریستوران پر غور کر سکتے ہیں؟", "type": "polite_conflict_with_alternative", "keywords": ["allergic", "italian", "indian", "instead"]}, {"response": "I''m fine with Italian, but I''m also open to other suggestions.", "response_urdu": "میں اطالوی کے ساتھ ٹھیک ہوں، لیکن میں دوسری تجاویز کے لیے بھی کھلا ہوں۔", 