INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '5.1.1', 19, 'Should artificial intelligence be used in education?', 'کیا مصنوعی ذہانت کو تعلیم میں استعمال کرنا چاہیے؟', 'Practice debating complex topics', 'پیچیدہ موضوعات پر بحث کی مشق', 1, '{"id": 1, "topic": "Should artificial intelligence be used in education?", "topic_urdu": "کیا مصنوعی ذہانت کو تعلیم میں استعمال کرنا چاہیے؟", "ai_position": "AI should not replace human teachers but can enhance learning experiences.", "ai_position_urdu": "مصنوعی ذہانت کو انسانی اساتذہ کی جگہ نہیں لینی چاہیے لیکن یہ سیکھنے کے تجربات کو بہتر بنا سکتی ہے۔", "category": "Technology & Education", "difficulty": "C1", "speaking_duration": "2-3 minutes", "thinking_time": "30 seconds", "expected_structure": "Introduction → Main Argument → Counter-Argument → Evidence → Conclusion", "expected_keywords": ["artificial intelligence", "education", "enhancement", "human interaction", "personalized learning", "critical thinking", "emotional intelligence", "technological advancement", "traditional methods", "digital literacy"], "expected_keywords_urdu": ["مصنوعی ذہانت", "تعلیم", "بہتری", "انسانی تعامل", "ذاتی سیکھنا", "تنقیدی سوچ", "جذباتی ذہانت", "ٹیکنالوجی کی ترقی", "روایتی طریقے", "ڈیجیٹل خواندگی"], "vocabulary_focus": ["pedagogical", "algorithmic", "adaptive", "comprehensive", "methodological", "cognitive", "interactive", "innovative", "systematic", "analytical"], "vocabulary_focus_urdu": ["تعلیمی", "الگورتھم", "مطابقت پذیر", "جامع", "طریقہ کار", "ادراکی", "تعاملی", "اختراعی", "منظم", "تجزیاتی"], "model_response": "I believe AI should be integrated into education as a complementary tool rather than a replacement for human teachers. Firstly, AI can provide personalized learning experiences that adapt to individual student needs and learning styles. However, it''s crucial to maintain the human element in education, as teachers offer emotional support, mentorship, and the ability to inspire critical thinking that AI cannot replicate. In contrast, AI excels at processing vast amounts of data and identifying patterns that can help educators make informed decisions about curriculum development and student progress.", "model_response_urdu": "میں سمجھتا ہوں کہ مصنوعی ذہانت کو انسانی اساتذہ کی جگہ لینے کے بجائے تعلیم میں ایک تکمیلی آلے کے طور پر شامل کرنا چاہیے۔ سب سے پہلے، مصنوعی ذہانت ذاتی سیکھنے کے تجربات فراہم کر سکتی ہے جو انفرادی طالب علم کی ضروریات اور سیکھنے کے انداز کے مطابق ڈھل جاتے ہیں۔ تاہم، تعلیم میں انسانی عنصر کو برقرار رکھنا ضروری ہے، کیونکہ اساتذہ جذباتی مدد، رہنمائی، اور تنقیدی سوچ کو متاثر کرنے کی صلاحیت فراہم کرتے ہیں جو مصنوعی ذہانت نہیں کر سکتی۔ اس کے برعکس، مصنوعی ذہانت بڑی مقدار میں ڈیٹا کو پروسیس کرنے اور ان پیٹرنز کی شناخت کرنے میں ماہر ہے جو تعلیمی کارکنوں کو نصاب کی ترقی اور طالب علم کی پیشرفت کے بارے میں باخبر فیصلے کرنے میں مدد کر سکتے ہیں۔", "evaluation_criteria": {"argument_structure": 25, "critical_thinking": 25, "vocabulary_range": 20, "fluency_grammar": 20, "discourse_markers": 10}, "learning_objectives": ["Develop structured argumentation skills", "Practice critical thinking and analysis", "Use advanced academic vocabulary", "Master discourse markers and connectors", "Express complex ideas with clarity"], "type": "debate"}', 1, 'Technology & Education', 'C1'),
('topic', '5.1.2', 19, 'Is freedom more important than security in today''s world?', 'کیا آج کی دنیا میں آزادی سیکیورٹی سے زیادہ اہم ہے؟', 'Practice debating complex topics', 'پیچیدہ موضوعات پر بحث کی مشق', 2, '{"id": 2, "topic": "Is freedom more important than security in today''s world?", "topic_urdu": "کیا آج کی دنیا میں آزادی سیکیورٹی سے زیادہ اہم ہے؟", "ai_position": "Security must be prioritized to protect fundamental freedoms.", "ai_position_urdu": "بنیادی آزادیوں کی حفاظت کے لیے سیکیورٹی کو ترجیح دی جانی چاہیے۔", "category": "Philosophy & Society", "difficulty": "C1", "speaking_duration": "2-3 minutes", "thinking_time": "30 seconds", "expected_structure": "Context → Position Statement → Supporting Arguments → Counter-Arguments → Synthesis", "expected_keywords": ["freedom", "security", "civil liberties", "surveillance", "privacy", "democracy", "authoritarianism", "balance", "rights", "responsibilities"], "expected_keywords_urdu": ["آزادی", "سیکیورٹی", "شہری آزادیاں", "نگرانی", "رازداری", "جمہوریت", "آمریت", "توازن", "حقوق", "ذمہ داریاں"], "vocabulary_focus": ["fundamental", "surveillance", "authoritarian", "democratic", "legitimate", "arbitrary", "proportional", "constitutional", "sovereign", "autonomous"], "vocabulary_focus_urdu": ["بنیادی", "نگرانی", "آمرانہ", "جمہوری", "جائز", "من مانی", "متناسب", "آئینی", "خود مختار", "خود کار"], "model_response": "This is a complex philosophical question that requires careful consideration of both individual rights and collective welfare. While freedom is undoubtedly a fundamental human right, I believe that in today''s interconnected world, security measures are necessary to protect those very freedoms. However, the implementation of security measures must be proportional and transparent to prevent the erosion of civil liberties. The challenge lies in finding the right balance where security enhances rather than restricts our freedoms. Furthermore, we must distinguish between legitimate security concerns and authoritarian overreach that uses security as a pretext for control.", "model_response_urdu": "یہ ایک پیچیدہ فلسفیانہ سوال ہے جو انفرادی حقوق اور اجتماعی بہبود دونوں کے محتاط جائزے کی ضرورت رکھتا ہے۔ اگرچہ آزادی بلا شبہ ایک بنیادی انسانی حق ہے، لیکن میں سمجھتا ہوں کہ آج کی باہم جڑی ہوئی دنیا میں، انہی آزادیوں کی حفاظت کے لیے سیکیورٹی کے اقدامات ضروری ہیں۔ تاہم، سیکیورٹی کے اقدامات کا نفاذ متناسب اور شفاف ہونا چاہیے تاکہ شہری آزادیوں کے خاتمے کو روکا جا سکے۔ چیلنج یہ ہے کہ صحیح توازن تلاش کیا جائے جہاں سیکیورٹی ہماری آزادیوں کو محدود کرنے کے بجائے انہیں بہتر بنائے۔ مزید برآں، ہمیں جائز سیکیورٹی کے خدشات اور آمرانہ زیادتی کے درمیان فرق کرنا چاہیے جو کنٹرول کے بہانے کے طور پر سیکیورٹی کا استعمال کرتی ہے۔", "evaluation_criteria": {"argument_structure": 25, "critical_thinking": 25, "vocabulary_range": 20, "fluency_grammar": 20, "discourse_markers": 10}, "learning_objectives": ["Analyze complex philosophical concepts", "Present balanced arguments", "Use sophisticated vocabulary", "Demonstrate nuanced understanding", "Express abstract ideas clearly"], "type": "debate"}', 2, 'Philosophy & Society', 'C1'),
('topic', '5.1.3', 19, 'Does money equal success in modern society?', 'کیا جدید معاشرے میں پیسہ کامیابی کے برابر ہے؟', 'Practice debating complex topics', 'پیچیدہ موضوعات پر بحث کی مشق', 3, '{"id": 3, "topic": "Does money equal success in modern society?", "topic_urdu": "کیا جدید معاشرے میں پیسہ کامیابی کے برابر ہے؟", "ai_position": "Success encompasses multiple dimensions beyond financial wealth.", "ai_position_urdu": "کامیابی مالی دولت سے آگے کے متعدد پہلوؤں پر محیط ہے۔", "category": "Philosophy & Economics", "difficulty": "C1", "speaking_duration": "2-3 minutes", "thinking_time": "30 seconds", "expected_structure": "Definition → Multiple Perspectives → Evidence → Personal View → Conclusion", "expected_keywords": ["success", "wealth", "happiness", "fulfillment", "purpose", "achievement", "materialism", "well-being", "values", "priorities"], "expected_keywords_urdu": ["کامیابی", "دولت", "خوشی", "اطمینان", "مقصد", "کامیابی", "مادیت پسندی", "بہبود", "اقدار", "ترجیحات"], "vocabulary_focus": ["materialistic", "intrinsic", "extrinsic", "fulfillment", "purpose", "legacy", "contribution", "well-being", "authentic", "meaningful"], "vocabulary_focus_urdu": ["مادیت پسند", "داخلی", "خارجی", "اطمینان", "مقصد", "ورثہ", "شراکت", "بہبود", "اصلی", "معنی خیز"], "model_response": "The equation of money with success represents a reductionist view that fails to capture the multifaceted nature of human achievement and fulfillment. While financial stability is undoubtedly important for meeting basic needs and providing opportunities, true success encompasses various dimensions including personal growth, meaningful relationships, intellectual development, and contribution to society. Furthermore, research consistently shows that beyond a certain threshold, increased wealth does not correlate with increased happiness. Success should be measured by the positive impact one has on others and the fulfillment derived from pursuing one''s passions and values, rather than merely accumulating material possessions.", "model_response_urdu": "پیسے کو کامیابی کے ساتھ مساوی کرنا ایک تقلیل پسندانہ نظریہ ہے جو انسانی کامیابی اور اطمینان کی کثیر جہتی نوعیت کو پکڑنے میں ناکام ہے۔ اگرچہ مالی استحکام بنیادی ضروریات کو پورا کرنے اور مواقع فراہم کرنے کے لیے بلا شبہ اہم ہے، لیکن حقیقی کامیابی مختلف پہلوؤں پر محیط ہے جن میں ذاتی ترقی، معنی خیز تعلقات، فکری ترقی، اور معاشرے میں شراکت شامل ہیں۔ مزید برآں، تحقیق مسلسل دکھاتی ہے کہ ایک خاص حد سے آگے، بڑھتی ہوئی دولت خوشی میں اضافے سے مطابقت نہیں رکھتی۔ کامیابی کو دوسروں پر مثبت اثر اور اپنے جذبات اور اقدار کی پیروی سے حاصل ہونے والے اطمینان سے ماپا جانا چاہیے، نہ کہ صرف مادی اشیاء جمع کرنے سے۔", "evaluation_criteria": {"argument_structure": 25, "critical_thinking": 25, "vocabulary_range": 20, "fluency_grammar": 20, "discourse_markers": 10}, "learning_objectives": ["Challenge conventional wisdom", "Present evidence-based arguments", "Use abstract and philosophical vocabulary", "Demonstrate critical analysis", "Express nuanced perspectives"], "type": "debate"}', 3, 'Philosophy & Economics', 'C1'),
('topic', '5.1.4', 19, 'Should social media platforms be regulated by governments?', 'کیا سوشل میڈیا پلیٹ فارمز کو حکومتوں کے ذریعے منظم کرنا چاہیے؟', 'Practice debating complex topics', 'پیچیدہ موضوعات پر بحث کی مشق', 4, '{"id": 4, "topic": "Should social media platforms be regulated by governments?", "topic_urdu": "کیا سوشل میڈیا پلیٹ فارمز کو حکومتوں کے ذریعے منظم کرنا چاہیے؟", "ai_position": "Limited regulation is necessary to protect users while preserving innovation.", "ai_position_urdu": "صارفین کی حفاظت کے لیے محدود ضابطہ کاری ضروری ہے جبکہ اختراع کو برقرار رکھا جائے۔", "category": "Technology & Governance", "difficulty": "C1", "speaking_duration": "2-3 minutes", "thinking_time": "30 seconds", "expected_structure": "Problem Statement → Stakeholder Analysis → Regulatory Options → Recommendations → Implementation", "expected_keywords": ["regulation", "social media", "privacy", "misinformation", "innovation", "democracy", "censorship", "transparency", "accountability", "freedom of expression"], "expected_keywords_urdu": ["ضابطہ کاری", "سوشل میڈیا", "رازداری", "غلط معلومات", "اختراع", "جمہوریت", "سینسرشپ", "شفافیت", "جوابدہی", "اظہار رائے کی آزادی"], "vocabulary_focus": ["algorithmic", "disinformation", "misinformation", "transparency", "accountability", "censorship", "moderation", "compliance", "oversight", "stakeholder"], "vocabulary_focus_urdu": ["الگورتھم", "غلط معلومات", "غلط معلومات", "شفافیت", "جوابدہی", "سینسرشپ", "اعتدال", "تعمیل", "نگرانی", "حصہ دار"], "model_response": "The regulation of social media platforms presents a complex challenge that requires balancing multiple competing interests. On one hand, these platforms have become essential public spaces for discourse, necessitating protection of free expression and innovation. On the other hand, the spread of misinformation, privacy violations, and algorithmic manipulation require some form of oversight. I believe that targeted regulation focusing on transparency, accountability, and user protection is necessary, while avoiding overly restrictive measures that could stifle innovation or enable government censorship. The key is implementing regulations that address specific harms without compromising the fundamental benefits these platforms provide to democratic discourse.", "model_response_urdu": "سوشل میڈیا پلیٹ فارمز کی ضابطہ کاری ایک پیچیدہ چیلنج پیش کرتی ہے جو متعدد مقابلہ جاتی مفادات کے توازن کی ضرورت رکھتی ہے۔ ایک طرف، یہ پلیٹ فارمز گفتگو کے لیے ضروری عوامی جگہیں بن گئے ہیں، جس کے لیے آزاد اظہار اور اختراع کی حفاظت ضروری ہے۔ دوسری طرف، غلط معلومات کا پھیلاؤ، رازداری کی خلاف ورزی، اور الگورتھم کا استحصال کسی نہ کسی شکل میں نگرانی کی ضرورت رکھتے ہیں۔ میں سمجھتا ہوں کہ شفافیت، جوابدہی، اور صارف کی حفاظت پر مرکوز ہدف شدہ ضابطہ کاری ضروری ہے، جبکہ زیادہ محدود اقدامات سے بچنا چاہیے جو اختراع کو روک سکتے ہیں یا حکومتی سینسرشپ کو ممکن بنا سکتے ہیں۔ کلید یہ ہے کہ ایسے ضوابط نافذ کیے جائیں جو مخصوص نقصانات کو حل کریں بغیر ان بنیادی فوائد سے سمجھوتہ کیے جو یہ پلیٹ فارم جمہوری گفتگو کو فراہم کرتے ہیں۔", "evaluation_criteria": {"argument_structure": 25, "critical_thinking": 25, "vocabulary_range": 20, "fluency_grammar": 20, "discourse_markers": 10}, "learning_objectives": ["Analyze complex policy issues", "Present balanced regulatory perspectives", "Use technical and policy vocabulary", "Demonstrate nuanced understanding", "Express sophisticated arguments"], "type": "debate"}', 4, 'Technology & Governance', 'C1'),
('topic', '5.1.5', 19, 'Is the traditional university education model still relevant?', 'کیا روایتی یونیورسٹی تعلیم کا ماڈل اب بھی متعلقہ ہے؟', 'Practice debating complex topics', 'پیچیدہ موضوعات پر بحث کی مشق', 5, '{"id": 5, "topic": "Is the traditional university education model still relevant?", "topic_urdu": "کیا روایتی یونیورسٹی تعلیم کا ماڈل اب بھی متعلقہ ہے؟", "ai_position": "The traditional model needs significant adaptation to remain relevant in the digital age.", "ai_position_urdu": "ڈیجیٹل دور میں متعلقہ رہنے کے لیے روایتی ماڈل کو اہم مطابقت کی ضرورت ہے۔", "category": "Education & Technology", "difficulty": "C1", "speaking_duration": "2-3 minutes", "thinking_time": "30 seconds", "expected_structure": "Historical Context → Current Challenges → Adaptation Strategies → Future Vision → Recommendations", "expected_keywords": ["university", "education", "digital transformation", "online learning", "traditional methods", "innovation", "accessibility", "cost-effectiveness", "quality", "adaptation"], "expected_keywords_urdu": ["یونیورسٹی", "تعلیم", "ڈیجیٹل تبدیلی", "آن لائن سیکھنا", "روایتی طریقے", "اختراع", "دستیابی", "لاگت کی تاثیر", "معیار", "مطابقت"], "vocabulary_focus": ["pedagogical", "methodological", "transformative", "disruptive", "hybrid", "blended", "interactive", "collaborative", "experiential", "adaptive"], "vocabulary_focus_urdu": ["تعلیمی", "طریقہ کار", "تبدیلی", "خلل انداز", "مخلوط", "ملایا ہوا", "تعاملی", "تعاونی", "تجرباتی", "مطابقت پذیر"], "model_response": "The traditional university model, while historically valuable, requires substantial evolution to meet the demands of the 21st century. While the core elements of critical thinking, research skills, and intellectual discourse remain essential, the delivery methods and accessibility need significant transformation. The rise of digital technologies, changing job market demands, and increasing costs necessitate a hybrid approach that combines the best of traditional pedagogy with innovative digital tools. However, we must be careful not to lose the invaluable aspects of face-to-face interaction, mentorship, and the development of social and emotional intelligence that traditional universities uniquely provide.", "model_response_urdu": "روایتی یونیورسٹی ماڈل، اگرچہ تاریخی طور پر قیمتی ہے، کو 21ویں صدی کی ضروریات کو پورا کرنے کے لیے اہم ارتقاء کی ضرورت ہے۔ اگرچہ تنقیدی سوچ، تحقیق کی مہارتیں، اور فکری گفتگو کے بنیادی عناصر ضروری رہتے ہیں، لیکن ترسیل کے طریقے اور دستیابی کو اہم تبدیلی کی ضرورت ہے۔ ڈیجیٹل ٹیکنالوجیز کا عروج، تبدیل ہوتی ہوئی ملازمت کی مارکیٹ کی ضرورت، اور بڑھتی ہوئی لاگت ایک مخلوط طریقہ کار کی ضرورت رکھتے ہیں جو روایتی تعلیم کے بہترین پہلوؤں کو اختراعی ڈیجیٹل اوزاروں کے ساتھ جوڑتا ہے۔ تاہم، ہمیں احتیاط کرنی چاہیے کہ روایتی یونیورسٹیوں کی منفرد فراہم کردہ آمنے سامنے تعامل، رہنمائی، اور سماجی اور جذباتی ذہانت کی ترقی کے انمول پہلوؤں کو نہ کھو دیں۔", "evaluation_criteria": {"argument_structure": 25, "critical_thinking": 25, "vocabulary_range": 20, "fluency_grammar": 20, "discourse_markers": 10}, "learning_objectives": ["Analyze institutional change", "Present balanced perspectives on tradition vs innovation", "Use academic and technical vocabulary", "Demonstrate forward-thinking analysis", "Express complex educational concepts"], "type": "debate"}', 5, 'Education & Technology', 'C1');

