INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '5.2.1', 20, 'The impact of climate change on future generations', 'موسمیاتی تبدیلی کا مستقبل کی نسلوں پر اثر', 'Practice academic presentation skills', 'تعلیمی پیشکش کی مہارتوں کی مشق', 1, '{"id": 1, "topic": "The impact of climate change on future generations", "topic_urdu": "موسمیاتی تبدیلی کا مستقبل کی نسلوں پر اثر", "category": "Environmental Science", "difficulty": "C1", "speaking_duration": "3 minutes", "thinking_time": "30 seconds", "expected_structure": "Introduction → Thesis Statement → Supporting Evidence → Counter-Arguments → Conclusion", "expected_keywords": ["climate change", "future generations", "environmental impact", "sustainability", "carbon emissions", "renewable energy", "policy changes", "global warming", "ecosystem", "intergenerational equity"], "expected_keywords_urdu": ["موسمیاتی تبدیلی", "مستقبل کی نسلیں", "ماحولیاتی اثر", "پائیداری", "کاربن اخراج", "قابل تجدید توانائی", "پالیسی تبدیلیاں", "عالمی گرمی", "ماحولیاتی نظام", "نسلی انصاف"], "vocabulary_focus": ["catastrophic", "irreversible", "mitigation", "adaptation", "anthropogenic", "biodiversity", "resilience", "sustainable development", "carbon footprint", "greenhouse gases"], "vocabulary_focus_urdu": ["تباہ کن", "ناقابل واپسی", "کمی", "مطابقت", "انسانی وجہ سے", "حیاتیاتی تنوع", "مزاحمت", "پائیدار ترقی", "کاربن فٹ پرنٹ", "گرین ہاؤس گیسز"], "model_response": "Climate change represents one of the most pressing challenges facing future generations. The scientific consensus is clear: human activities are driving unprecedented global warming, with potentially catastrophic consequences. Rising sea levels threaten coastal communities, extreme weather events are becoming more frequent, and biodiversity loss accelerates at alarming rates. However, we must acknowledge that the burden of climate change will fall disproportionately on future generations who had no role in creating this crisis. The concept of intergenerational equity demands that we take immediate action. Transitioning to renewable energy sources, implementing carbon pricing mechanisms, and investing in sustainable infrastructure are not merely policy choices but moral imperatives. While some argue that such measures are economically costly, the long-term costs of inaction far exceed the investments required for mitigation. Future generations deserve a habitable planet, and we have both the responsibility and the capability to ensure they inherit one.", "model_response_urdu": "موسمیاتی تبدیلی مستقبل کی نسلوں کے سامنے آنے والے سب سے اہم چیلنجز میں سے ایک ہے۔ سائنسی اتفاق رائے واضح ہے: انسانی سرگرمیاں بے مثال عالمی گرمی کو جنم دے رہی ہیں، جس کے ممکنہ طور پر تباہ کن نتائج ہیں۔ بڑھتی ہوئی سمندری سطحیں ساحلی برادریوں کو خطرے میں ڈال رہی ہیں، شدید موسمی واقعات زیادہ کثرت سے ہو رہے ہیں، اور حیاتیاتی تنوع کی کمی خطرناک شرح سے بڑھ رہی ہے۔ تاہم، ہمیں تسلیم کرنا چاہیے کہ موسمیاتی تبدیلی کا بوجھ مستقبل کی نسلوں پر غیر متناسب طور پر پڑے گا جن کا اس بحران کو پیدا کرنے میں کوئی کردار نہیں تھا۔ نسلی انصاف کا تصور مطالبہ کرتا ہے کہ ہم فوری کارروائی کریں۔ قابل تجدید توانائی کے ذرائع کی طرف منتقلی، کاربن قیمتوں کے میکانزم کو نافذ کرنا، اور پائیدار بنیادی ڈھانچے میں سرمایہ کاری صرف پالیسی کے انتخاب نہیں بلکہ اخلاقی ضرورتیں ہیں۔ جبکہ کچھ لوگ دلیل دیتے ہیں کہ ایسی تدابیر معاشی طور پر مہنگی ہیں، عدم کارروائی کی طویل مدتی لاگت کمی کے لیے درکار سرمایہ کاری سے کہیں زیادہ ہے۔ مستقبل کی نسلوں کو ایک قابل رہائش سیارہ ملنا چاہیے، اور ہمارے پاس یہ یقینی بنانے کی ذمہ داری اور صلاحیت دونوں ہیں کہ وہ ایک وراثت میں پائیں۔", "evaluation_criteria": {"argument_structure": 25, "evidence_usage": 25, "academic_tone": 20, "fluency_pacing": 15, "vocabulary_range": 15}, "learning_objectives": ["Develop structured academic argumentation skills", "Use evidence and statistics effectively in presentations", "Master formal academic tone and vocabulary", "Practice time-bound logical structuring", "Build confidence in delivering extended academic speeches"], "type": "academic"}', 1, 'Environmental Science', 'C1'),
('topic', '5.2.2', 20, 'The future of remote work in global companies', 'عالمی کمپنیوں میں دور دراز کام کا مستقبل', 'Practice academic presentation skills', 'تعلیمی پیشکش کی مہارتوں کی مشق', 2, '{"id": 2, "topic": "The future of remote work in global companies", "topic_urdu": "عالمی کمپنیوں میں دور دراز کام کا مستقبل", "category": "Business & Technology", "difficulty": "C1", "speaking_duration": "3 minutes", "thinking_time": "30 seconds", "expected_structure": "Context → Main Arguments → Supporting Data → Challenges → Future Outlook", "expected_keywords": ["remote work", "global companies", "flexibility", "productivity", "workplace", "digital transformation", "work-life balance", "collaboration", "technology", "organizational culture"], "expected_keywords_urdu": ["دور دراز کام", "عالمی کمپنیاں", "لچک", "پیداواری صلاحیت", "کام کی جگہ", "ڈیجیٹل تبدیلی", "کام اور زندگی کا توازن", "تعاون", "ٹیکنالوجی", "تنظیمی ثقافت"], "vocabulary_focus": ["hybrid model", "digital nomad", "productivity metrics", "employee engagement", "organizational efficiency", "cost reduction", "talent acquisition", "workplace flexibility", "digital infrastructure", "remote collaboration"], "vocabulary_focus_urdu": ["ہائبرڈ ماڈل", "ڈیجیٹل خانہ بدوش", "پیداواری صلاحیت کے پیمانے", "ملازم کی شمولیت", "تنظیمی کارکردگی", "لاگت میں کمی", "صلاحیت کی حصول", "کام کی جگہ کی لچک", "ڈیجیٹل بنیادی ڈھانچہ", "دور دراز تعاون"], "model_response": "The COVID-19 pandemic has fundamentally transformed how global companies approach work, accelerating the adoption of remote work models that were previously considered experimental. This shift represents not merely a temporary adaptation but a permanent restructuring of workplace dynamics. Research indicates that 70% of companies plan to maintain hybrid work arrangements, with employees working both remotely and in-office. The benefits are substantial: companies report 25% increases in productivity, significant reductions in operational costs, and expanded access to global talent pools. However, this transition presents challenges that require careful management. Maintaining organizational culture and fostering collaboration across distributed teams demands innovative approaches to team building and communication. Furthermore, the digital divide creates inequities in remote work accessibility, requiring companies to invest in technology infrastructure and training. The future of work will likely involve flexible hybrid models that balance the benefits of remote work with the collaborative advantages of in-person interaction. Companies that successfully navigate this transition will gain competitive advantages in talent retention and operational efficiency.", "model_response_urdu": "کوویڈ-19 کی وبا نے بنیادی طور پر تبدیل کر دیا ہے کہ عالمی کمپنیاں کام کے بارے میں کس طرح سوچتی ہیں، دور دراز کام کے ماڈلز کی اپنائیت کو تیز کر دیا ہے جو پہلے تجرباتی سمجھے جاتے تھے۔ یہ تبدیلی صرف ایک عارضی مطابقت نہیں بلکہ کام کی جگہ کی حرکیات کا مستقل تنظیمی ڈھانچہ ہے۔ تحقیق سے پتہ چلتا ہے کہ 70% کمپنیاں ہائبرڈ کام کی ترتیبات کو برقرار رکھنے کا منصوبہ رکھتی ہیں، جہاں ملازمین دور دراز اور دفتر میں دونوں جگہ کام کرتے ہیں۔ فوائد قابل ذکر ہیں: کمپنیاں پیداواری صلاحیت میں 25% اضافے، آپریشنل لاگت میں نمایاں کمی، اور عالمی صلاحیت کے ذخائر تک بڑھتی ہوئی رسائی کی رپورٹ کرتی ہیں۔ تاہم، یہ تبدیلی چیلنجز پیش کرتی ہے جو احتیاط سے انتظام کی ضرورت رکھتے ہیں۔ تنظیمی ثقافت کو برقرار رکھنا اور تقسیم شدہ ٹیموں میں تعاون کو فروغ دینا ٹیم بنانے اور مواصلات کے اختراعی طریقوں کا مطالبہ کرتا ہے۔ مزید برآں، ڈیجیٹل تقسیم دور دراز کام کی رسائی میں عدم مساوات پیدا کرتی ہے، جس کے لیے کمپنیوں کو ٹیکنالوجی کے بنیادی ڈھانچے اور تربیت میں سرمایہ کاری کی ضرورت ہے۔ کام کا مستقبل شاید لچکدار ہائبرڈ ماڈلز شامل کرے گا جو دور دراز کام کے فوائد کو آمنے سامنے تعامل کے تعاونی فوائد کے ساتھ متوازن کرتے ہیں۔ جو کمپنیاں اس تبدیلی کو کامیابی سے طے کرتی ہیں وہ صلاحیت کی برقراری اور آپریشنل کارکردگی میں مسابقتی فوائد حاصل کریں گی۔", "evaluation_criteria": {"argument_structure": 25, "evidence_usage": 25, "academic_tone": 20, "fluency_pacing": 15, "vocabulary_range": 15}, "learning_objectives": ["Present complex business concepts with clarity", "Use statistical evidence to support arguments", "Demonstrate professional presentation skills", "Structure extended academic discourse", "Develop confidence in formal business communication"], "type": "academic"}', 2, 'Business & Technology', 'C1'),
('topic', '5.2.3', 20, 'The role of education in shaping society', 'معاشرے کی تشکیل میں تعلیم کا کردار', 'Practice academic presentation skills', 'تعلیمی پیشکش کی مہارتوں کی مشق', 3, '{"id": 3, "topic": "The role of education in shaping society", "topic_urdu": "معاشرے کی تشکیل میں تعلیم کا کردار", "category": "Education & Sociology", "difficulty": "C1", "speaking_duration": "3 minutes", "thinking_time": "30 seconds", "expected_structure": "Introduction → Historical Context → Current Impact → Future Implications → Conclusion", "expected_keywords": ["education", "society", "social mobility", "economic development", "critical thinking", "citizenship", "innovation", "equality", "knowledge", "social change"], "expected_keywords_urdu": ["تعلیم", "معاشرہ", "سماجی نقل و حرکت", "معاشی ترقی", "تنقیدی سوچ", "شہریت", "اختراع", "مساوات", "علم", "سماجی تبدیلی"], "vocabulary_focus": ["socioeconomic", "pedagogical", "curriculum development", "cognitive development", "social capital", "educational equity", "lifelong learning", "academic achievement", "social cohesion", "intellectual growth"], "vocabulary_focus_urdu": ["سماجی معاشی", "تعلیمی", "نصاب کی ترقی", "ادراکی ترقی", "سماجی سرمایہ", "تعلیمی انصاف", "عمر بھر سیکھنا", "تعلیمی کامیابی", "سماجی اتحاد", "فکری ترقی"], "model_response": "Education serves as the cornerstone of societal development, functioning as both a mirror reflecting current social values and a catalyst for future transformation. Throughout history, educational systems have been instrumental in shaping social structures, economic opportunities, and cultural norms. The relationship between education and social mobility is well-documented: individuals with higher educational attainment experience greater economic success and social influence. However, education''s impact extends far beyond individual advancement. It cultivates critical thinking skills essential for democratic participation, fosters social cohesion through shared knowledge and values, and drives innovation that propels economic development. The quality and accessibility of education directly influence a society''s capacity for progress and its ability to address complex challenges. In contemporary society, education must adapt to prepare students for rapidly evolving technological and social landscapes. This requires not only updating curricula to include digital literacy and global awareness but also developing pedagogical approaches that encourage creativity, collaboration, and problem-solving. The future of society depends on our ability to create educational systems that are both inclusive and innovative, ensuring that all members can contribute meaningfully to collective progress.", "model_response_urdu": "تعلیم سماجی ترقی کی بنیاد کے طور پر کام کرتی ہے، جو موجودہ سماجی اقدار کو عکس کرنے والا آئینہ اور مستقبل کی تبدیلی کا محرک دونوں کے طور پر کام کرتی ہے۔ پوری تاریخ میں، تعلیمی نظام سماجی ڈھانچے، معاشی مواقع، اور ثقافتی اصولوں کی تشکیل میں اہم رہے ہیں۔ تعلیم اور سماجی نقل و حرکت کے درمیان تعلق اچھی طرح سے دستاویزی ہے: اعلیٰ تعلیمی حصول والے افراد زیادہ معاشی کامیابی اور سماجی اثر کا تجربہ کرتے ہیں۔ تاہم، تعلیم کا اثر انفرادی ترقی سے کہیں آگے بڑھتا ہے۔ یہ جمہوری شرکت کے لیے ضروری تنقیدی سوچ کی مہارتوں کو فروغ دیتی ہے، مشترکہ علم اور اقدار کے ذریعے سماجی اتحاد کو فروغ دیتی ہے، اور اختراع کو جنم دیتی ہے جو معاشی ترقی کو آگے بڑھاتی ہے۔ تعلیم کی معیار اور رسائی براہ راست معاشرے کی ترقی کی صلاحیت اور پیچیدہ چیلنجز سے نمٹنے کی اس کی صلاحیت کو متاثر کرتی ہے۔ معاصر معاشرے میں، تعلیم کو تیزی سے بدلتے ہوئے تکنیکی اور سماجی مناظر کے لیے طلباء کو تیار کرنے کے لیے مطابقت اختیار کرنی چاہیے۔ اس کے لیے نہ صرف نصاب کو اپ ڈیٹ کرنے کی ضرورت ہے تاکہ ڈیجیٹل خواندگی اور عالمی آگاہی شامل ہو بلکہ تعلیمی طریقوں کو فروغ دینے کی بھی ضرورت ہے جو تخلیقی صلاحیت، تعاون، اور مسئلہ حل کرنے کو حوصلہ دیتے ہیں۔ معاشرے کا مستقبل ہماری صلاحیت پر منحصر ہے کہ ہم ایسے تعلیمی نظام بنائیں جو شمولیت اور اختراع دونوں ہوں، یہ یقینی بناتے ہوئے کہ تمام ارکان اجتماعی ترقی میں معنی خیز حصہ ڈال سکیں۔", "evaluation_criteria": {"argument_structure": 25, "evidence_usage": 25, "academic_tone": 20, "fluency_pacing": 15, "vocabulary_range": 15}, "learning_objectives": ["Analyze complex social phenomena academically", "Present theoretical concepts with practical examples", "Use sophisticated academic vocabulary appropriately", "Structure arguments with clear logical progression", "Demonstrate understanding of interdisciplinary topics"], "type": "academic"}', 3, 'Education & Sociology', 'C1'),
('topic', '5.2.4', 20, 'Is social media making us smarter or dumber?', 'کیا سوشل میڈیا ہمیں زیادہ ذہین یا زیادہ بیوقوف بنا رہا ہے؟', 'Practice academic presentation skills', 'تعلیمی پیشکش کی مہارتوں کی مشق', 4, '{"id": 4, "topic": "Is social media making us smarter or dumber?", "topic_urdu": "کیا سوشل میڈیا ہمیں زیادہ ذہین یا زیادہ بیوقوف بنا رہا ہے؟", "category": "Technology & Psychology", "difficulty": "C1", "speaking_duration": "3 minutes", "thinking_time": "30 seconds", "expected_structure": "Introduction → Cognitive Effects → Social Impact → Balanced Analysis → Conclusion", "expected_keywords": ["social media", "cognitive development", "information overload", "attention span", "digital literacy", "critical thinking", "addiction", "communication", "learning", "distraction"], "expected_keywords_urdu": ["سوشل میڈیا", "ادراکی ترقی", "معلومات کی زیادتی", "توجہ کی مدت", "ڈیجیٹل خواندگی", "تنقیدی سوچ", "لت", "مواصلات", "سیکھنا", "توجہ ہٹانا"], "vocabulary_focus": ["cognitive load", "information processing", "digital distraction", "multitasking", "selective attention", "cognitive bias", "digital literacy", "information literacy", "media literacy", "cognitive enhancement"], "vocabulary_focus_urdu": ["ادراکی بوجھ", "معلومات کی پروسیسنگ", "ڈیجیٹل توجہ ہٹانا", "متعدد کام", "انتخابی توجہ", "ادراکی تعصب", "ڈیجیٹل خواندگی", "معلومات کی خواندگی", "میڈیا خواندگی", "ادراکی بہتری"], "model_response": "The impact of social media on cognitive development represents a complex interplay between technological advancement and human psychology. Research suggests that social media usage has both positive and negative effects on cognitive functioning. On the positive side, social media platforms provide unprecedented access to diverse information sources, fostering digital literacy and global awareness. Users develop skills in information processing, multitasking, and digital communication that are increasingly valuable in modern society. However, concerns about cognitive effects are well-founded. Studies indicate that excessive social media use correlates with decreased attention spans, reduced deep reading comprehension, and increased susceptibility to cognitive biases. The constant stream of notifications and information fragments can lead to cognitive overload, making it difficult to engage in sustained, focused thinking. The key question is not whether social media makes us smarter or dumber, but how we can leverage its benefits while mitigating its cognitive costs. This requires developing digital literacy skills, setting boundaries for usage, and cultivating mindfulness in our online interactions. The future of cognitive development depends on our ability to use social media as a tool for enhancement rather than allowing it to become a source of cognitive degradation.", "model_response_urdu": "ادراکی ترقی پر سوشل میڈیا کا اثر تکنیکی ترقی اور انسانی نفسیات کے درمیان پیچیدہ تعامل کی نمائندگی کرتا ہے۔ تحقیق سے پتہ چلتا ہے کہ سوشل میڈیا کا استعمال ادراکی کام کرنے پر مثبت اور منفی دونوں اثرات رکھتا ہے۔ مثبت پہلو پر، سوشل میڈیا پلیٹ فارمز مختلف معلومات کے ذرائع تک بے مثال رسائی فراہم کرتے ہیں، ڈیجیٹل خواندگی اور عالمی آگاہی کو فروغ دیتے ہیں۔ صارفین معلومات کی پروسیسنگ، متعدد کام، اور ڈیجیٹل مواصلات کی مہارتیں تیار کرتے ہیں جو جدید معاشرے میں تیزی سے قیمتی ہیں۔ تاہم، ادراکی اثرات کے بارے میں خدشات اچھی طرح سے بنیاد رکھتے ہیں۔ مطالعات سے پتہ چلتا ہے کہ سوشل میڈیا کا زیادہ استعمال کم توجہ کی مدت، گہری پڑھنے کی سمجھ میں کمی، اور ادراکی تعصبات کے لیے بڑھتی ہوئی حساسیت کے ساتھ منسلک ہے۔ نوٹیفیکیشنز اور معلومات کے ٹکڑوں کا مسلسل بہاؤ ادراکی بوجھ کا باعث بن سکتا ہے، جس سے مسلسل، مرکوز سوچ میں مشغول ہونا مشکل ہو جاتا ہے۔ اہم سوال یہ نہیں ہے کہ سوشل میڈیا ہمیں زیادہ ذہین یا زیادہ بیوقوف بناتا ہے، بلکہ یہ ہے کہ ہم اس کے ادراکی اخراجات کو کم کرتے ہوئے اس کے فوائد کا فائدہ کیسے اٹھا سکتے ہیں۔ اس کے لیے ڈیجیٹل خواندگی کی مہارتیں تیار کرنے، استعمال کے لیے حدود طے کرنے، اور ہماری آن لائن تعاملات میں ذہن سازی کو فروغ دینے کی ضرورت ہے۔ ادراکی ترقی کا مستقبل ہماری صلاحیت پر منحصر ہے کہ ہم سوشل میڈیا کو بہتری کے آلے کے طور پر استعمال کریں بجائے اس کے کہ ہم اسے ادراکی تنزلی کا ذریعہ بننے دیں۔", "evaluation_criteria": {"argument_structure": 25, "evidence_usage": 25, "academic_tone": 20, "fluency_pacing": 15, "vocabulary_range": 15}, "learning_objectives": ["Evaluate complex technological impacts critically", "Present balanced arguments with evidence", "Use psychological and technological terminology", "Demonstrate nuanced understanding of modern issues", "Develop skills in presenting controversial topics objectively"], "type": "academic"}', 4, 'Technology & Psychology', 'C1'),
('topic', '5.2.5', 20, 'The ethics of artificial intelligence in healthcare', 'صحت کی دیکھ بھال میں مصنوعی ذہانت کی اخلاقیات', 'Practice academic presentation skills', 'تعلیمی پیشکش کی مہارتوں کی مشق', 5, '{"id": 5, "topic": "The ethics of artificial intelligence in healthcare", "topic_urdu": "صحت کی دیکھ بھال میں مصنوعی ذہانت کی اخلاقیات", "category": "Medical Ethics & Technology", "difficulty": "C1", "speaking_duration": "3 minutes", "thinking_time": "30 seconds", "expected_structure": "Introduction → Benefits → Ethical Concerns → Regulatory Framework → Future Recommendations", "expected_keywords": ["artificial intelligence", "healthcare", "medical ethics", "patient privacy", "diagnostic accuracy", "algorithmic bias", "informed consent", "medical decision-making", "data security", "human oversight"], "expected_keywords_urdu": ["مصنوعی ذہانت", "صحت کی دیکھ بھال", "طبی اخلاقیات", "مریض کی رازداری", "تشخیصی درستگی", "الگورتھم تعصب", "باخبر رضامندی", "طبی فیصلہ سازی", "ڈیٹا سیکیورٹی", "انسانی نگرانی"], "vocabulary_focus": ["algorithmic transparency", "clinical decision support", "predictive analytics", "medical malpractice", "informed consent", "data governance", "clinical validation", "ethical oversight", "patient autonomy", "medical liability"], "vocabulary_focus_urdu": ["الگورتھم شفافیت", "کلینیکل فیصلہ کی حمایت", "پیش گوئی تجزیہ", "طبی غلطی", "باخبر رضامندی", "ڈیٹا حکمرانی", "کلینیکل توثیق", "اخلاقی نگرانی", "مریض کی خود مختاری", "طبی ذمہ داری"], "model_response": "The integration of artificial intelligence in healthcare represents a paradigm shift in medical practice, offering unprecedented opportunities for improved diagnosis, treatment, and patient care. However, this technological advancement raises profound ethical questions that require careful consideration. AI systems can analyze vast amounts of medical data to identify patterns and predict outcomes with remarkable accuracy, potentially saving countless lives through early detection and personalized treatment plans. Yet, the ethical implications are complex and multifaceted. Patient privacy concerns arise from the collection and processing of sensitive medical data, while algorithmic bias can perpetuate existing healthcare disparities. The question of informed consent becomes complicated when patients may not fully understand how AI systems influence their care decisions. Furthermore, the delegation of medical decision-making to algorithms raises questions about accountability and liability in cases of diagnostic errors or treatment complications. Establishing appropriate regulatory frameworks is essential to ensure that AI enhances rather than compromises patient care. This requires balancing innovation with ethical safeguards, ensuring transparency in algorithmic decision-making, and maintaining human oversight in critical medical decisions. The future of AI in healthcare depends on our ability to develop systems that are not only technologically advanced but also ethically sound and socially responsible.", "model_response_urdu": "صحت کی دیکھ بھال میں مصنوعی ذہانت کا انضمام طبی عمل میں ایک مثال کی تبدیلی کی نمائندگی کرتا ہے، جو بہتر تشخیص، علاج، اور مریض کی دیکھ بھال کے لیے بے مثال مواقع فراہم کرتا ہے۔ تاہم، یہ تکنیکی ترقی گہرے اخلاقی سوالات اٹھاتی ہے جو احتیاط سے غور کی ضرورت رکھتے ہیں۔ مصنوعی ذہانت کے نظام بڑی مقدار میں طبی ڈیٹا کا تجزیہ کر سکتے ہیں تاکہ پیٹرنز کی شناخت کریں اور قابل ذکر درستگی کے ساتھ نتائج کی پیش گوئی کریں، ممکنہ طور پر ابتدائی شناخت اور ذاتی علاج کے منصوبوں کے ذریعے لاکھوں زندگیاں بچا سکتے ہیں۔ تاہم، اخلاقی مضمرات پیچیدہ اور کثیر جہتی ہیں۔ مریض کی رازداری کے خدشات حساس طبی ڈیٹا کے جمع کرنے اور پروسیسنگ سے پیدا ہوتے ہیں، جبکہ الگورتھم تعصب موجودہ صحت کی دیکھ بھال کی عدم مساوات کو برقرار رکھ سکتا ہے۔ باخبر رضامندی کا سوال پیچیدہ ہو جاتا ہے جب مریض مکمل طور پر نہیں سمجھ سکتے کہ مصنوعی ذہانت کے نظام ان کی دیکھ بھال کے فیصلوں کو کیسے متاثر کرتے ہیں۔ مزید برآں، طبی فیصلہ سازی کو الگورتھمز کے حوالے کرنا جوابدہی اور ذمہ داری کے سوالات اٹھاتا ہے تشخیصی غلطیوں یا علاج کی پیچیدگیوں کے معاملات میں۔ مناسب ضابطہ کاری کے فریم ورک قائم کرنا ضروری ہے تاکہ یہ یقینی بنایا جا سکے کہ مصنوعی ذہانت مریض کی دیکھ بھال کو کمزور کرنے کے بجائے بہتر بنائے۔ اس کے لیے اختراع کو اخلاقی حفاظتی تدابیر کے ساتھ متوازن کرنے، الگورتھم فیصلہ سازی میں شفافیت کو یقینی بنانے، اور اہم طبی فیصلوں میں انسانی نگرانی کو برقرار رکھنے کی ضرورت ہے۔ صحت کی دیکھ بھال میں مصنوعی ذہانت کا مستقبل ہماری صلاحیت پر منحصر ہے کہ ہم ایسے نظام تیار کریں جو نہ صرف تکنیکی طور پر جدید ہوں بلکہ اخلاقی طور پر درست اور سماجی طور پر ذمہ دار بھی ہوں۔", "evaluation_criteria": {"argument_structure": 25, "evidence_usage": 25, "academic_tone": 20, "fluency_pacing": 15, "vocabulary_range": 15}, "learning_objectives": ["Analyze complex ethical dilemmas systematically", "Present medical and technological concepts clearly", "Use specialized vocabulary in appropriate contexts", "Structure arguments with logical coherence", "Demonstrate understanding of interdisciplinary ethical issues"], "type": "academic"}', 5, 'Medical Ethics & Technology', 'C1'),
('topic', '5.2.6', 20, 'The future of renewable energy and sustainable development', 'قابل تجدید توانائی اور پائیدار ترقی کا مستقبل', 'Practice academic presentation skills', 'تعلیمی پیشکش کی مہارتوں کی مشق', 6, '{"id": 6, "topic": "The future of renewable energy and sustainable development", "topic_urdu": "قابل تجدید توانائی اور پائیدار ترقی کا مستقبل", "category": "Environmental Science & Economics", "difficulty": "C1", "speaking_duration": "3 minutes", "thinking_time": "30 seconds", "expected_structure": "Introduction → Current State → Technological Advances → Economic Implications → Future Vision", "expected_keywords": ["renewable energy", "sustainable development", "climate change", "economic growth", "technological innovation", "energy transition", "green economy", "carbon neutrality", "energy security", "environmental protection"], "expected_keywords_urdu": ["قابل تجدید توانائی", "پائیدار ترقی", "موسمیاتی تبدیلی", "معاشی ترقی", "تکنیکی اختراع", "توانائی کی منتقلی", "سبز معیشت", "کاربن غیر جانبدار", "توانائی کی سیکیورٹی", "ماحولیاتی تحفظ"], "vocabulary_focus": ["energy infrastructure", "grid modernization", "energy storage", "smart grids", "energy efficiency", "carbon pricing", "green technology", "energy independence", "sustainable infrastructure", "clean energy transition"], "vocabulary_focus_urdu": ["توانائی کا بنیادی ڈھانچہ", "گرڈ کی جدید کاری", "توانائی کی ذخیرہ کاری", "سمارٹ گرڈز", "توانائی کی کارکردگی", "کاربن کی قیمت", "سبز ٹیکنالوجی", "توانائی کی آزادی", "پائیدار بنیادی ڈھانچہ", "صاف توانائی کی منتقلی"], "model_response": "The transition to renewable energy represents the most significant economic and environmental transformation of our time. As we confront the dual challenges of climate change and energy security, renewable energy technologies have emerged as both a necessity and an opportunity for sustainable development. The cost of renewable energy has plummeted dramatically over the past decade, with solar and wind power now competitive with fossil fuels in many markets. This economic viability, combined with technological advances in energy storage and grid modernization, has created unprecedented momentum for the energy transition. However, the path to a fully renewable energy future is complex and requires careful planning. The intermittent nature of renewable energy sources necessitates significant investments in energy storage infrastructure and smart grid technologies. Furthermore, the transition must be managed to ensure energy security and economic stability during the transformation period. The economic benefits of renewable energy extend beyond environmental protection. The renewable energy sector has become a major driver of job creation and economic growth, with investments in clean energy infrastructure generating substantial returns. Countries that lead in renewable energy development are positioning themselves for long-term economic competitiveness in the emerging green economy. The future of sustainable development depends on our ability to accelerate this transition while ensuring that the benefits are distributed equitably across society.", "model_response_urdu": "قابل تجدید توانائی کی طرف منتقلی ہمارے وقت کی سب سے اہم معاشی اور ماحولیاتی تبدیلی کی نمائندگی کرتی ہے۔ جیسے ہی ہم موسمیاتی تبدیلی اور توانائی کی سیکیورٹی کے دوہرے چیلنجز کا سامنا کرتے ہیں، قابل تجدید توانائی کی ٹیکنالوجیز پائیدار ترقی کے لیے ضرورت اور موقع دونوں کے طور پر سامنے آئی ہیں۔ قابل تجدید توانائی کی لاگت پچھلے دہائی میں ڈرامائی طور پر کم ہوئی ہے، جہاں شمسی اور ہوا کی طاقت اب بہت سے مارکیٹس میں فوسل فیول کے ساتھ مسابقتی ہے۔ یہ معاشی قابلیت، توانائی کی ذخیرہ کاری اور گرڈ کی جدید کاری میں تکنیکی ترقی کے ساتھ مل کر، توانائی کی منتقلی کے لیے بے مثال رفتار پیدا کر چکی ہے۔ تاہم، مکمل طور پر قابل تجدید توانائی کے مستقبل کا راستہ پیچیدہ ہے اور احتیاط سے منصوبہ بندی کی ضرورت رکھتا ہے۔ قابل تجدید توانائی کے ذرائع کی عارضی نوعیت توانائی کی ذخیرہ کاری کے بنیادی ڈھانچے اور سمارٹ گرڈ ٹیکنالوجیز میں اہم سرمایہ کاری کی ضرورت رکھتی ہے۔ مزید برآں، تبدیلی کے دوران توانائی کی سیکیورٹی اور معاشی استحکام کو یقینی بنانے کے لیے منتقلی کا انتظام کرنا چاہیے۔ قابل تجدید توانائی کے معاشی فوائد ماحولیاتی تحفظ سے آگے بڑھتے ہیں۔ قابل تجدید توانائی کا شعبہ روزگار کی تخلیق اور معاشی ترقی کا اہم محرک بن گیا ہے، جہاں صاف توانائی کے بنیادی ڈھانچے میں سرمایہ کاری سے قابل ذکر واپسی حاصل ہو رہی ہے۔ جو ممالک قابل تجدید توانائی کی ترقی میں قیادت کر رہے ہیں وہ ابھرتی ہوئی سبز معیشت میں طویل مدتی معاشی مسابقت کے لیے اپنے آپ کو پوزیشن کر رہے ہیں۔ پائیدار ترقی کا مستقبل ہماری صلاحیت پر منحصر ہے کہ ہم اس منتقلی کو تیز کریں جبکہ یہ یقینی بنائیں کہ فوائد معاشرے میں مساوی طور پر تقسیم ہوں۔", "evaluation_criteria": {"argument_structure": 25, "evidence_usage": 25, "academic_tone": 20, "fluency_pacing": 15, "vocabulary_range": 15}, "learning_objectives": ["Present complex environmental and economic concepts", "Use statistical data and trends effectively", "Demonstrate understanding of interdisciplinary topics", "Structure arguments with clear cause-effect relationships", "Develop skills in presenting policy-relevant information"], "type": "academic"}', 6, 'Environmental Science & Economics', 'C1'),
('topic', '5.2.7', 20, 'The impact of globalization on cultural identity', 'عالمگیریت کا ثقافتی شناخت پر اثر', 'Practice academic presentation skills', 'تعلیمی پیشکش کی مہارتوں کی مشق', 7, '{"id": 7, "topic": "The impact of globalization on cultural identity", "topic_urdu": "عالمگیریت کا ثقافتی شناخت پر اثر", "category": "Cultural Studies & Sociology", "difficulty": "C1", "speaking_duration": "3 minutes", "thinking_time": "30 seconds", "expected_structure": "Introduction → Definition → Positive Effects → Negative Effects → Synthesis", "expected_keywords": ["globalization", "cultural identity", "cultural exchange", "cultural homogenization", "cultural diversity", "traditional values", "modernization", "cultural preservation", "cross-cultural communication", "cultural adaptation"], "expected_keywords_urdu": ["عالمگیریت", "ثقافتی شناخت", "ثقافتی تبادلہ", "ثقافتی یکسانیت", "ثقافتی تنوع", "روایتی اقدار", "جدید کاری", "ثقافتی تحفظ", "بین الثقافتی مواصلات", "ثقافتی مطابقت"], "vocabulary_focus": ["cultural hybridity", "glocalization", "cultural imperialism", "cultural relativism", "ethnocentrism", "cultural appropriation", "cultural resilience", "intercultural dialogue", "cultural authenticity", "cultural evolution"], "vocabulary_focus_urdu": ["ثقافتی مخلوطیت", "گلوکلائزیشن", "ثقافتی سامراجیت", "ثقافتی نسبیت", "نسلی مرکزیت", "ثقافتی تخصیص", "ثقافتی مزاحمت", "بین الثقافتی مکالمہ", "ثقافتی اصالت", "ثقافتی ارتقاء"], "model_response": "Globalization has fundamentally transformed the way we understand and experience cultural identity in the contemporary world. This complex phenomenon has created both opportunities for cultural enrichment and challenges to traditional cultural preservation. On one hand, globalization has facilitated unprecedented cultural exchange, allowing individuals to access diverse cultural perspectives, traditions, and practices from around the world. This exposure has enriched our understanding of human diversity and fostered cross-cultural dialogue and cooperation. However, the homogenizing effects of globalization pose significant challenges to cultural identity. The dominance of Western cultural products and values in global markets has raised concerns about cultural imperialism and the erosion of local traditions. Many communities struggle to maintain their cultural authenticity while participating in the global economy. The concept of glocalization, which combines global and local elements, offers a potential solution to this dilemma. This approach allows communities to adapt global influences while preserving their unique cultural heritage. The key to navigating globalization''s impact on cultural identity lies in finding the right balance between cultural openness and preservation. This requires developing cultural literacy, promoting intercultural dialogue, and creating policies that support cultural diversity while facilitating global cooperation. The future of cultural identity in a globalized world depends on our ability to celebrate diversity while building bridges of understanding across cultural boundaries.", "model_response_urdu": "عالمگیریت نے بنیادی طور پر تبدیل کر دیا ہے کہ ہم معاصر دنیا میں ثقافتی شناخت کو کیسے سمجھتے اور تجربہ کرتے ہیں۔ یہ پیچیدہ رجحان نے ثقافتی بہتری کے مواقع اور روایتی ثقافتی تحفظ کے چیلنجز دونوں پیدا کیے ہیں۔ ایک طرف، عالمگیریت نے بے مثال ثقافتی تبادلے کو آسان بنایا ہے، جو افراد کو دنیا بھر سے مختلف ثقافتی نقطہ ہائے نظر، روایات، اور طریقوں تک رسائی کی اجازت دیتا ہے۔ یہ نمائش نے انسانی تنوع کی ہماری سمجھ کو بہتر بنایا ہے اور بین الثقافتی مکالمہ اور تعاون کو فروغ دیا ہے۔ تاہم، عالمگیریت کے یکسانیت کے اثرات ثقافتی شناخت کے لیے اہم چیلنجز پیش کرتے ہیں۔ عالمی مارکیٹس میں مغربی ثقافتی مصنوعات اور اقدار کی بالادستی نے ثقافتی سامراجیت اور مقامی روایات کے کٹاؤ کے بارے میں خدشات پیدا کیے ہیں۔ بہت سی برادریاں عالمی معیشت میں حصہ لیتے ہوئے اپنی ثقافتی اصالت کو برقرار رکھنے کے لیے جدوجہد کرتی ہیں۔ گلوکلائزیشن کا تصور، جو عالمی اور مقامی عناصر کو جوڑتا ہے، اس مشکل کا ایک ممکنہ حل پیش کرتا ہے۔ یہ طریقہ برادریوں کو عالمی اثرات کے مطابق ڈھالنے کی اجازت دیتا ہے جبکہ ان کی منفرد ثقافتی وراثت کو برقرار رکھتا ہے۔ ثقافتی شناخت پر عالمگیریت کے اثرات کو طے کرنے کی کلید ثقافتی کھلے پن اور تحفظ کے درمیان صحیح توازن تلاش کرنا ہے۔ اس کے لیے ثقافتی خواندگی تیار کرنے، بین الثقافتی مکالمہ کو فروغ دینے، اور ایسی پالیسیاں بنانے کی ضرورت ہے جو عالمی تعاون کو آسان بناتے ہوئے ثقافتی تنوع کی حمایت کرتی ہیں۔ عالمگیر دنیا میں ثقافتی شناخت کا مستقبل ہماری صلاحیت پر منحصر ہے کہ ہم ثقافتی حدود کے پار تفہیم کے پل بناتے ہوئے تنوع کا جشن منائیں۔", "evaluation_criteria": {"argument_structure": 25, "evidence_usage": 25, "academic_tone": 20, "fluency_pacing": 15, "vocabulary_range": 15}, "learning_objectives": ["Analyze complex cultural phenomena critically", "Present balanced arguments on controversial topics", "Use sociological and cultural terminology appropriately", "Demonstrate understanding of global-local dynamics", "Develop skills in presenting nuanced cultural analysis"], "type": "academic"}', 7, 'Cultural Studies & Sociology', 'C1');

